import React, { useState } from "react";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import DeleteIcon from "./Icon";
import "./Shifter.css";
import { StepContext } from "@mui/material";

const Shifter = (props) => {
  const [shifters, setShifters] = useState(props.data);

  const deleteItemHandler = (id) => {
    setShifters ((shifters) => {
      const updatedShifters = shifters.filter (shifter => shifter.id !== id);
      return updatedShifters;
    })
    return fetch("http://127.0.0.1:5000", {method:'POST', headers:{'Content-Type':'application/json'}})
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          props.parsedData(data);
          setIsClicked(true);
        });
  };

  return (
    <div>
      {props.data.map((shifter) => {
        const shifterId = shifter.id;
        const shifterTime = shifter.time;
        const shifterName = shifter.employee;
        return (
          <Grid
            container
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            className="myBorder"
          >
            <Grid item>
              <Typography variant="h6">
                {shifterTime} <span>&nbsp;&nbsp;&nbsp;</span> {shifterName}
              </Typography>
            </Grid>
            <Grid item>
              <DeleteIcon
                onDelete={deleteItemHandler}
                data={shifter.id}

              />
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};
export default Shifter;
