import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext, ItemsContext } from "./Context";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import { Typography } from "@mui/material";

const RadioButton = (props) => {
  const { userToken, setUserToken } = useContext(UserContext);
  const { setWhoWork } = useContext(ItemsContext);
  const navigate = useNavigate();

  const onChangeHandler = async (e) => {
    console.log("TEST 2nd");
    console.log(e.target.value);
    const response = await fetch("http://127.0.0.1:5000/main", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + userToken,
      },
      body: JSON.stringify(e.target.value),
    });
    if (response.status === 400) {
      console.log("session expired -> redirect from POST");
      setUserToken();
      navigate("/");
    } else {
      const data = await response.json();
      setWhoWork(data[0]);
      props.setData(data[1]);
    }
  };
  return (
    <FormControl sx={{ ml: "180px" }}>
      <FormLabel sx={{ color: "black", fontSize: "20px" }}>
        How many night shifters?
      </FormLabel>
      <RadioGroup row value={props.data} onChange={onChangeHandler}>
        <FormControlLabel value="1" control={<Radio />} label="1" />
        <FormControlLabel value="2" control={<Radio />} label="2" />
      </RadioGroup>
    </FormControl>
  );
};
export default RadioButton;
