import { useState } from "react";
import FormInput from "./FormInput";
import { Container, Header, Error } from "./Styles/Login.styled";
import StrButton from "./Button";

const Login = () => {
    const[loginError, setLoginError] = useState()
  const [values, setValues] = useState({
    username: "",
    password: "",
    companyCode: "",
  });

  const inputs = [
    {
      id: 1,
      name: "username",
      type: "text",
      label: "Username",
    },
    {
      id: 2,
      name: "password",
      type: "password",
      label: "Password",
    },
    {
      id: 3,
      name: "company_code",
      type: "text",
      label: "Company Code",
    },
  ];

  async function onClickHandler() {
    console.log(values);
    fetch("http://127.0.0.1:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
            "username": values.username,
            "password": values.password,
            "companyCode": values.companyCode
    }),
      }).then((response) =>{
        if (response.status === 200) {
            return response.json();
        }
        else {
            console.log(response.status)
            setLoginError(response.json())
        }

      })
  };

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
    setLoginError();

  };
  return (
    <Container>
      <form>
        <Header>Login</Header>
        <Error>{loginError ? loginError : ""}</Error>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            type={input.type}
            name={input.name}
            label={input.label}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <StrButton onClick={onClickHandler} />
      </form>
    </Container>
  );
};

export default Login;
