import styled from "styled-components";

export const Container = styled.div`
  float: left;
  margin: 0;
  height: auto;
  width: auto;
`;

export const ListItems = styled.div`
  display: block;
  float: left;
  padding: 5px 0;
  width: 100%;
`;

export const H3 = styled.h3`
  display: block;
  float: left;
  margin-top: 5px;
  padding: 0;
  width: 80%;
`;

export const Icon = styled.div`
  float: right;
  margin-top: 5px;
  padding: 0;
  display: block;
  border-radius: 11px;
  cursor: pointer;
  &:hover {
    transform: scale(1.2);
  }
`;
