import { useContext, useState } from "react";
import { ItemsContext } from "./Context";
import { Container, Text } from "./Styles/Shifter.styled";
import { Button } from "@mui/material";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";

const Shifter = (props) => {
  const { whoWork } = useContext(ItemsContext);
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const deleteItemHandler = (id) => {
    const removedShifter = whoWork.filter((shifter) => shifter.id === id);
    console.log(id);
    props.onChange(removedShifter);
  };

  return (
    <div>
      {whoWork.map((shifter) => (
        <Container key={shifter.id}>
          <Text>
            {shifter.time} <span>&nbsp;&nbsp;&nbsp;</span>
            {shifter.employee}
          </Text>
          <Button onClick={() => deleteItemHandler(shifter.id)}>
            <HighlightOffIcon fontSize="medium" />
          </Button>
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle>How many night shifters?</DialogTitle>
            <DialogContent>
              <DialogContentText id="alert-dialog-description">
                Let Google help apps determine location. This means sending
                anonymous location data to Google, even when no apps are
                running.
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose}>Disagree</Button>
              <Button onClick={handleClose} autoFocus>
                Agree
              </Button>
            </DialogActions>
          </Dialog>
        </Container>
      ))}
    </div>
  );
};
export default Shifter;
