import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext, ItemsContext } from "./Context";
import { Backdrop, CircularProgress, Fade } from "@mui/material";
import Alert from "@mui/material/Alert";
import Shifter from "./Shifter";
import Buttons from "./Buttons";
import ShifterText from "./ShifterText";
import { Container, Tabs, TextTabs } from "./Styles/Items.styled";

const Items = () => {
  const { userToken } = useContext(UserContext);
  const navigate = useNavigate();
  const [whoWork, setWhoWork] = useState();
  const [open, setOpen] = useState(true);
  const [deletedShifter, setDeletedShifter] = useState(null);
  const [scheduleText, setScheduleText] = useState(null);
  const [flag, setFlag] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://127.0.0.1:5000/main", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + userToken,
        },
      });
      const data = await response.json();
      setWhoWork(data);
      setOpen(false);
    };
    if (!userToken) {
      console.log(userToken);
      navigate("/");
    }
    fetchData();
  }, []);

  useEffect(() => {
    const updateDataFetch = async () => {
      console.log(deletedShifter);
      const response = await fetch("http://127.0.0.1:5000/main", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + userToken,
        },
        body: JSON.stringify(deletedShifter),
      });
      if (!response.ok) {
        <Fade in={flag} timeout={3000}>
          <Alert severity="error">This is an error alert â€” check it out!</Alert>
        </Fade>;
      } else {
        const data = await response.json();
        setWhoWork(data);
      }
      if (deletedShifter !== null) {
        updateDataFetch();
      }
    };
  }, [deletedShifter]);

  return (
    <div>
      {!whoWork && (
        <Backdrop open={open} color="#DCDCDC">
          <CircularProgress color="inherit" />
          <h1>Fetching</h1>
        </Backdrop>
      )}
      {whoWork && (
        <Container>
          <ItemsContext.Provider value={{ whoWork, setWhoWork }}>
            <Tabs>
              <Shifter onChange={setDeletedShifter} />
            </Tabs>
            <TextTabs>
              <ShifterText slack={setScheduleText} />
            </TextTabs>
            <Buttons />
          </ItemsContext.Provider>
        </Container>
      )}
    </div>
  );

  // async function onButtonClickHandler() {}

  //   const { WebClient } = require("@slack/web-api");
  //   const client = new WebClient(
  //     "xoxb-63217127925-4111504931396-zeCzTsLRawTdBtxdwnIC3QCu"
  //   );
  //   const channelId = "G9UNDRZBR";

  //   try {
  //     const result = await client.chat.postMessage({
  //       channel: channelId,
  //       text: JSON.stringify(scheduleText),
  //     });
  //     console.log(result);}
  //     catch (error) {
  //       console.error(error)
  // }}

  //   fetch(
  //     "https://hooks.slack.com/services/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",
  //     {
  //       method: "POST",
  //       headers: { "Content-type": "application/x-www-form-urlencoded" },
  //       body: JSON.stringify({ text: scheduleText }),
  //       redirect: "follow",
  //     }
  //   )
  //     .then((response) => response.text())
  //     .catch((error) => console.log("error", error));
  // };
};

export default Items;
