import { useState } from "react";
import FormInput from "./FormInput";
import { Container, Header } from "./Styles/Login.styled";
import MyButton from "./Button";

const Login = () => {
  const [values, setValues] = useState({
    username: "",
    password: "",
    companyCode: "",
  });

  const inputs = [
    {
      id: 1,
      name: "username",
      type: "text",
      placeholder: "Username",
      label: "Username",
    },
    {
      id: 2,
      name: "password",
      type: "password",
      placeholder: "Password",
      label: "Password",
    },
    {
      id: 3,
      name: "company_code",
      type: "text",
      placeholder: "Company Code",
      label: "Company Code",
    },
  ];

  const onSubmitHandler = (e) => {
    e.preventDefault();
  };
  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };
  return (
    <Container>
      <form onSubmit={onSubmitHandler}>
        <Header>Login</Header>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            type={input.type}
            label={input.label}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <button>Submit</button>
      </form>

    </Container>
  );
};

export default Login;
