import React, { useState, useEffect } from "react";
import Shifter from "./Shifter";
import ShifterText from "./ShifterText";
import { Container, Tabs, TextTabs, Button, ButtonText } from "./Styles/Items.styled";

const Items = (props) => {
  const [whoWork, setWhoWork] = useState(props.data);
  const [deletedShifter, setDeletedShifter] = useState(null);
  const [scheduleText, setScheduleText] = useState(null)


  useEffect(() => {
    if (deletedShifter !== null) {
      fetch("http://127.0.0.1:5000", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(deletedShifter),
      })
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setWhoWork(data);
        });
    }
  }, [deletedShifter]);

  const onButtonClickHandler = () => {


    fetch(
      "https://hooks.slack.com/services/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",
      {
        method: "POST",
        headers: { "Content-type": "application/x-www-form-urlencoded" },
        body: JSON.stringify({ text: scheduleText }),
        redirect: "follow",
      }
    )
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  const onChangeHandler = (removedShifter) => {
    setDeletedShifter(removedShifter);
  };

  const slackHandler = (text) => {
    setScheduleText(text)
  } 

  return (
    <Container>
      <Tabs>
        <Shifter data={whoWork} onChange={onChangeHandler} />
      </Tabs>
      <TextTabs>
        <ShifterText data={whoWork} slack={slackHandler}/>
      </TextTabs>
      <Button onClick={onButtonClickHandler}>
        <ButtonText> Send to Slack</ButtonText>
      </Button>
    </Container>
  );
};

export default Items;
