import { useEffect, useContext } from "react";
import { ItemsContext } from "./Context";
import DeleteIcon from "./DeleteIcon";
import { Container, Text } from "./Styles/Shifter.styled";

const Shifter = (props) => {
  const { whoWork, setWhoWork } = useContext(ItemsContext);

  // useEffect(() => {
  //   setShifters(props.data);
  // }, [props]);

  const deleteItemHandler = (id) => {
    const removedShifter = whoWork.filter((shifter) => shifter.id === id);
    props.onChange(removedShifter);
  };

  return (
    <div>
      {whoWork.map((shifter) => (
          <Container key={shifter.id}>
            <Text>
              {shifter.time} <span>&nbsp;&nbsp;&nbsp;</span>
              {shifter.employee}
            </Text>

            <DeleteIcon onDelete={deleteItemHandler} id={shifter.id}/>
          </Container>
        ))
      }
    </div>
  );
};
export default Shifter;
