import React, { useState, useEffect } from "react";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import DeleteIcon from "./Icon";
import "./Shifter.css";

const Shifter = (props) => {

  const [shifters, setShifters] = useState(props.data);
  const [deletedShifter, setDeletedShifter] = useState('');

  useEffect(() => {
    if (deletedShifter !== null) {
      fetch("http://127.0.0.1:5000", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(deletedShifter),
      })
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setShifters(data);
        });
    }
    props.onChange(shifters);
  }, [deletedShifter]);

  const deleteItemHandler = (id) => {
    const removedShifter = shifters.filter((shifter) => shifter.id === id);
    setDeletedShifter(removedShifter);
  };

  return (
    <div>
      {shifters.map((shifter) => {
        const shifterId = shifter.id;
        const shifterTime = shifter.time;
        const shifterName = shifter.employee;
        return (
          <Grid
            container
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            className="myBorder"
          >
            <Grid item>
              <Typography variant="h6">
                {shifterTime} <span>&nbsp;&nbsp;&nbsp;</span> {shifterName}
              </Typography>
            </Grid>
            <Grid item>
              <DeleteIcon onDelete={deleteItemHandler} data={shifterId} />
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};
export default Shifter;
