import { useContext, useState } from "react";
import { ItemsContext } from "./Context";
import { Container, Text } from "./Styles/Shifter.styled";
import { Button } from "@mui/material";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogTitle from "@mui/material/DialogTitle";
import FormControl from "@mui/material/FormControl";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import FormControlLabel from "@mui/material/FormControlLabel";

const Shifter = (props) => {
  const { whoWork } = useContext(ItemsContext);
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const deleteItemHandler = (id) => {
    const removedShifter = whoWork.filter((shifter) => shifter.id === id);
    console.log(id);
    props.onChange(removedShifter);
  };

  return (
    <div>
      {whoWork.map((shifter) => (
        <Container key={shifter.id}>
          <Text>
            {shifter.time} <span>&nbsp;&nbsp;&nbsp;</span>
            {shifter.employee}
          </Text>
          <Button onClick={() => handleClickOpen}>
            <HighlightOffIcon fontSize="medium" />
          </Button>
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle>How many night shifters?</DialogTitle>
            <DialogContent>
              <FormControl>
                <RadioGroup row value={props.data} onChange={onChangeHandler}>
                  <FormControlLabel value="1" control={<Radio />} label="1" />
                  <FormControlLabel value="2" control={<Radio />} label="2" />
                </RadioGroup>
              </FormControl>
            </DialogContent>
            <DialogActions>
              <Button onClick={deleteItemHandler(shifter.id)}>Submit</Button>
            </DialogActions>
          </Dialog>
        </Container>
      ))}
    </div>
  );
};
export default Shifter;
