import styled from "styled-components";

export const Container = styled.div`
float: left;
// margin: 0;
display: block;
height: auto;
width: 100%;
border: 2px solid #2072d7b3;
background-color: white;
border-radius: 10px;`;

export const Text = styled.h4`
  float: left;
  display: block;
`;

export const Icon = styled.div`
  float: right;
  display: block;
`;
