import React, { useState, useEffect } from "react";
import { Container, ListItems, H3, Icon } from "./Styles/ShifterText.styled";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";

const ShifterText = (props) => {
  const [shifters, setShifters] = useState(props.data);

  useEffect(() => {
    setShifters(props.data);
  }, [props]);

  const sufix = (date) => {
    if (date > 20 || date < 10) {
      switch (date % 10) {
        case 1:
          return "st";
        case 2:
          return "nd";
        case 3:
          return "rd";
        default:
          return null;
      }
    }
    return "th";
  };

  const dateBuilder = () => {
    const newDate = new Date();
    const options = { weekday: "long", month: "long", day: "numeric" };
    const myDate =
      new Intl.DateTimeFormat("en-US", options).format(newDate) +
      sufix(newDate.getDate());
    return myDate;
  };

  const onClickHandler =() => {
    const date_text = "Queue Division - "+dateBuilder()+":\n";
    const shifters_text = shifters.map((shifter) => "\n".replace(",","")+shifter.employee +": " + shifter.time);
    const elem = shifters.toString();

    console.log(elem);

    // console.log(text);
  }

  return (
    <Container>
      <div>
        <H3>Queue Division - {dateBuilder()}:</H3>
        <Icon onClick={onClickHandler}>
          <ContentCopyIcon></ContentCopyIcon>
        </Icon>
      </div>
      {shifters.map((shifter) => {
        return (
          <ListItems key={shifter.id}>
            {shifter.employee}: {shifter.time}
          </ListItems>
        );
      })}
    </Container>
  );
};

export default ShifterText;
