import { useState, useEffect, useContext } from "react";
import { Container, ListItems, H3, Icon } from "./Styles/ShifterText.styled";
import { ItemsContext } from "./Context";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";
import { Tooltip } from "@mui/material";

const ShifterText = (props) => {
  const { whoWork, setWhoWork } = useContext(ItemsContext);
  const [copied, setCopied] = useState("Copy to clipboard");

  useEffect(() => {
    setShifters(props.data);
  }, [props]);

  const sufix = (date) => {
    if (date > 20 || date < 10) {
      switch (date % 10) {
        case 1:
          return "st";
        case 2:
          return "nd";
        case 3:
          return "rd";
        default:
          return "th";
      }
    }
  };

  const dateBuilder = () => {
    const newDate = new Date();
    const options = { weekday: "long", month: "long", day: "numeric" };
    const myDate =
      new Intl.DateTimeFormat("en-US", options).format(newDate) +
      sufix(newDate.getDate());
    return myDate;
  };
  const date_text = "Queue Division - " + dateBuilder() + ":\n\n";
  const shifters_text = shifters
    .map((shifter) => "*" + String(shifter.employee) + "*: " + shifter.time)
    .join("\n");
  const reminder_text =
    "\n\n:phone_green::phonecall: Please make sure you are all connected to Five9 :phone_green::phonecall:";

  props.slack(date_text + shifters_text + reminder_text);
  const onClickHandler = () => {
    navigator.clipboard.writeText(date_text + shifters_text + reminder_text);
    setCopied("Copied");
  };

  return (
    <Container>
      <div>
        <H3>Queue Division - {dateBuilder()}:</H3>
        <Icon onClick={onClickHandler}>
          <Tooltip title={copied} placement="top" arrow >
            <ContentCopyIcon />
          </Tooltip>
        </Icon>
      </div>
      {whoWork.map((shifter) => {
        return (
          <ListItems key={shifter.id}>
            {shifter.employee}: {shifter.time}
          </ListItems>
        );
      })}
    </Container>
  );
};

export default ShifterText;
