import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 100%;
  float: left;
  padding-top: 10px;
`;

export const Tabs = styled.ul`
  width: 45%;
  margin-right: 4%;
  margin-left: 1%;

  display: block;
  float: left;
  height: 100%;
`;

export const TextTabs = styled(Tabs)`
  border: 2px solid #2072d7b3;
  background-color: white;
  border-radius: 10px;
  padding: 10px;
`;

export const Button = styled.button`
  border-radius: 11px;
  margin-top: 10px;
  margin-left: 5%;
  background-color: #2072d7b3;
  max-width: 8000px;
  max-height: 50px;
  min-width: 500px;
  min-height: 50px;
  cursor: pointer;
  &:hover {
    box-shadow: 0 12px 16px 0 rgba(0, 0, 0, 0.24),
      0 17px 50px 0 rgba(0, 0, 0, 0.19);
  }
`;

export const ButtonText = styled.h1`
  color: #fffffff0;
  margin: auto;
  font-family: "Roboto", sans-serif;
  letter-spacing: 3px;
  font-size: 35px;
  text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000,
    1px 1px 0 #000;
`;
