    setDeletedShifter(removedShifter);
  };

  const slackHandler = (text) => {
    setScheduleText(text);
  };

  return (
    <div>
      {!whoWork && (
        <Backdrop open={open} color="#DCDCDC">
          <CircularProgress color="inherit" />
          <h1>Fetching</h1>
        </Backdrop>
      )}
      {whoWork && (
        <Container>
          <ItemsContext.Provider value={{whoWork, setWhoWork}}>
          <Tabs>
            <Shifter data={whoWork} onChange={onChangeHandler} />
          </Tabs>
          <TextTabs>
            <ShifterText data={whoWork} slack={slackHandler} />
          </TextTabs>
          <Button>
