import React, { useState, useEffect } from "react";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import DeleteIcon from "./Icon";
import "./Shifter.css";

const Shifter = (props) => {
  const [shifters, setShifters] = useState(props.data);

  useEffect(() => {
    setShifters(props.data)
  }, [props]);

  const deleteItemHandler = (id) => {
    const removedShifter = shifters.filter((shifter) => shifter.id === id);
    props.onChange(removedShifter);

  };

  return (
    <div>
      {shifters.map((shifter) => {
        return (
          <Grid
            container
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            className="myBorder"
          >
            <Grid item key={shifter.id}>
              <Typography variant="h6">
                {shifter.time} <span>&nbsp;&nbsp;&nbsp;</span> {shifter.employee}
              </Typography>
            </Grid>
            <Grid item>
              <DeleteIcon onDelete={deleteItemHandler} data={shifter.id} />
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};
export default Shifter;
