import React, {useState, useEffect} from "react";
import Shifter from "./Shifter";
import "./Items.css";
import ShifterText from "./ShifterText";

const Items = (props) => {

  const [whoWork, setWhoWork] = useState(props.data);

  const onChangeHandler = (data) => {
    setWhoWork(data);
  }
  useEffect(() => {setWhoWork(data);}, [whoWork]);

  return (
    <div className="item___main">
      <ul className="item___half">
        <Shifter data={whoWork} onChange={onChangeHandler}/>
      </ul>
      <ul className="item___half myTextItem">
        <ShifterText data={whoWork} />
      </ul>
    </div>
  );
};

export default Items;
